import base64
import subprocess

# Example base64-encoded Python code
# This example code, when decoded and executed, will simply print "Hello, World!"
base64_code = ""

def execute_decoded_base64(base64_string):
    try:
        # Decode the base64 string
        decoded_bytes = base64.b64decode(base64_string)
        decoded_string = decoded_bytes.decode('utf-8')

        # Execute the decoded string as Python code
        exec(decoded_string)
    except Exception as e:
        print(f"An error occurred: {e}")

execute_decoded_base64(base64_code)
